package id.ac.ui.cs.advprog.subscription.domains.states;import id.ac.ui.cs.advprog.subscription.domains.entities.Subscription;public class CancelState implements SubscriptionState {    private final Subscription subscription;    public CancelState(Subscription subscription) {        this.subscription = subscription;    }    @Override    public void activateSubscription() {        throw new IllegalStateException("can't activate a cancelled sub!");    }    @Override    public void cancelSubscription() {        subscription.setSubscriptionState(new CancelState(subscription));    }    @Override    public void pendingSubscription() {        throw new IllegalStateException("Can't pending an cancelled sub!");    }}